const data = [
    {
      title: 'The Lord of the Keys',
      subtitle: 'Collect all keys under the gunfire, and you are the lord of the KEYS!',
      link: 'https://github.com/yuehaoshi/LevelDesign',
      image: '/images/projects/LevelDesign.png',
      date: 'March 2022',
      desc:
      [
        'Try this excited action game and you will never regret!',
        'Developed a shooting game using Unreal4 Engine with health system, projectile system, movement system, collection system, restart system and enemy AI design and implementation',
        'Designed a user-friendly playing strategy by place guidance assets and inspiration mechanics during the game with four types of different perceptual enemy AI to interact with',
      ]
    },
    {
      title: 'Alien Invasion',
      subtitle: 'Tower Defense game developed in Unreal4',
      link: 'https://github.com/yuehaoshi/invasion-td',
      image: '/images/projects/Invasion.png',
      date: 'May 2022',
      desc:
      [
        'In this game, the player will have to strategically place towers along a main path to defend their home base from waves of enemies',
        'Each enemy will follow the path until it either dies or reaches the player’s base, at which point the player will lose health.',
      ]
    },
    {
      title: 'Infinite Matrix',
      subtitle: 'Start your advanture in the Matrix!',
      link: 'https://github.com/yuehaoshi/InfiniteMatrix',
      image: '/images/projects/InfinityMatrix.png',
      date: 'February 2022',
      desc:
      [
        'Come and explore the exciting matrix! You need to avoid hitting on the wall and the enemies, but your projectile can earn you score as well as destroying the enemies! Try how much score you can get!',
      ]
    },
  ];

